exec.command:
sudo curl -o /opt/solr/search_api_solr-7.x-1.10.tar.gz https://ftp.drupal.org/files/projects/search_api_solr-7.x-1.10.tar.gz;
sudo tar xvzf /opt/solr/search_api_solr-7.x-1.10.tar.gz -C /opt/solr;
